mapper.select:
select *
from employee_profiles
where company_id = #{companyId}
  and user_id = #{userId}
    limit 1

mapper.update:
update employee_profiles
department_id = #{departmentId}, employee_code = #{employeeCode}, employee_name = #{employeeName}, employee_email = #{employeeEmail}, employee_phone = #{employeePhone}, job_title = #{jobTitle}, manager_user_id = #{managerUserId}, start_date = #{startDate}, work_location = #{workLocation}, status = #{status}, updated_at = #{updatedAt}
where employee_id = #{employeeId}

UPDATE employee_profiles
SET
    status = #{status},
    updated_at = #{updatedAt}
WHERE company_id = #{companyId}
  AND user_id = #{userId}
