mapper.select:
select count(1)
from user_roles
where company_id = #{companyId}
  and user_id = #{userId}
  and role_code = #{roleCode}

mapper.insert:
insert into user_roles (
    user_role_id, company_id, user_id, role_code, created_at, updated_at
) values (
             #{userRoleId}, #{companyId}, #{userId}, #{roleCode}, #{createdAt}, #{updatedAt}
         )
