mapper.insert:

insert into checklist_instances (checklist_id, company_id, onboarding_id, 
  name, stage, status, 
  progress_percent, created_at, updated_at
  )
values (#{checklistId,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, #{onboardingId,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{stage,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{progressPercent,jdbcType=INTEGER}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP}
  )

mapper.select:
select checklist_id, company_id, onboarding_id, name, stage, status, progress_percent,
created_at, updated_at
from checklist_instances
where company_id = #{companyId,jdbcType=VARCHAR}
  and onboarding_id = #{onboardingId,jdbcType=VARCHAR}
order by created_at asc
