mapper.insert:

insert into survey_instances (survey_instance_id, company_id, onboarding_id, 
  survey_template_id, scheduled_at, sent_at, 
  closed_at, status, created_at
  )
values (#{surveyInstanceId,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, #{onboardingId,jdbcType=VARCHAR}, 
  #{surveyTemplateId,jdbcType=VARCHAR}, #{scheduledAt,jdbcType=TIMESTAMP}, #{sentAt,jdbcType=TIMESTAMP}, 
  #{closedAt,jdbcType=TIMESTAMP}, #{status,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}
  )

mapper.select:
select
survey_instance_id  as "surveyInstanceId",
survey_template_id  as "surveyTemplateId",
onboarding_id       as "onboardingId",
status              as "status",
sent_at             as "sentAt",
closed_at           as "closedAt"
from survey_instances
where company_id = #{companyId}

  and survey_template_id = #{templateId}


  and onboarding_id = #{onboardingId}


  and status = #{status}

order by sent_at desc
